function loadList(){void 0==localStorage.getItem("list")?addTask("Go on an adventure..."):$("main").html(localStorage.getItem("list"))}function saveList(){localStorage.setItem("list",$("main").html())}function addTask(e){$("main").append('<p class="task" id=""'+generateID()+'">'+e+"</p>"),saveList()}function generateID(){return Date.now()}function removeTask(e){$("#"+e).remove()}function toggleTask(e){switch($("#"+e).css("text-decoration")){case"none solid rgb(0, 0, 0)":$("#"+e).css("text-decoration","line-through");break;case"line-through solid rgb(0, 0, 0)":$("#"+e).css("text-decoration","none");break;default:$("#"+e).css("text-decoration","underline")}}$(document).ready(function(){});